import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../utils/api';

const Home = ({ user }) => {
  const [projects, setProjects] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    try {
      const { data } = await api.get('/projects');
      setProjects(data);
    } catch (error) {
      console.error(error);
    }
  };

  const handlePayment = (projectId) => {
    if (!user) {
      navigate('/login');
    } else {
      navigate(`/payment/${projectId}`);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Delete this project?')) {
      try {
        await api.delete(`/projects/${id}`);
        fetchProjects();
      } catch (error) {
        alert('Error deleting project');
      }
    }
  };

  return (
    <div className="container">
      <h2>Available Projects</h2>
      <div className="projects-grid">
        {projects.map(project => (
          <div key={project._id} className="project-card">
            <img src={`http://localhost:5000/uploads/${project.image}`} alt={project.title} />
            <div className="project-card-content">
              <h3>{project.title}</h3>
              <p>{project.description}</p>
              <div className="price">${project.price}</div>
              <a href={project.liveLink} target="_blank" rel="noopener noreferrer">View Live Demo</a>
              <button onClick={() => handlePayment(project._id)}>Purchase & Download</button>
              {user?.isAdmin && (
                <button className="btn-danger" onClick={() => handleDelete(project._id)}>Delete</button>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
