import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import api from '../utils/api';

const stripePromise = loadStripe('your_stripe_publishable_key_here');

const CheckoutForm = ({ projectId, project }) => {
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [paymentMethod, setPaymentMethod] = useState('card');
  const [upiId, setUpiId] = useState('');
  const [showScanner, setShowScanner] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      if (paymentMethod === 'card') {
        if (!stripe || !elements) return;

        const { data } = await api.post('/payments/create-payment-intent', { projectId });

        const result = await stripe.confirmCardPayment(data.clientSecret, {
          payment_method: { card: elements.getElement(CardElement) }
        });

        if (result.error) {
          alert(result.error.message);
        } else {
          await api.post('/payments/confirm', {
            projectId,
            paymentIntentId: result.paymentIntent.id
          });
          alert('Payment successful! Downloading...');
          window.location.href = `http://localhost:5000/api/projects/download/${projectId}`;
          navigate('/my-purchases');
        }
      } else if (paymentMethod === 'upi') {
        // Simulate UPI payment
        await api.post('/payments/confirm', {
          projectId,
          paymentIntentId: 'upi_' + Date.now()
        });
        alert('UPI Payment successful! Downloading...');
        window.location.href = `http://localhost:5000/api/projects/download/${projectId}`;
        navigate('/my-purchases');
      } else if (paymentMethod === 'scanner') {
        // Simulate QR Scanner payment
        await api.post('/payments/confirm', {
          projectId,
          paymentIntentId: 'qr_' + Date.now()
        });
        alert('QR Payment successful! Downloading...');
        window.location.href = `http://localhost:5000/api/projects/download/${projectId}`;
        navigate('/my-purchases');
      }
    } catch (error) {
      alert('Payment failed');
    }
    setLoading(false);
  };

  return (
    <form onSubmit={handleSubmit} className="form-container">
      <h2>Complete Payment</h2>
      {project && (
        <div style={{ marginBottom: '1.5rem', textAlign: 'center' }}>
          <h3>{project.title}</h3>
          <p className="price">${project.price}</p>
        </div>
      )}

      <div className="form-group">
        <label>Select Payment Method</label>
        <select 
          value={paymentMethod} 
          onChange={(e) => setPaymentMethod(e.target.value)}
          style={{ width: '100%', padding: '0.8rem', border: '1px solid #ddd', borderRadius: '4px' }}
        >
          <option value="card">Credit/Debit Card</option>
          <option value="upi">UPI Payment</option>
          <option value="scanner">QR Code Scanner</option>
        </select>
      </div>

      {paymentMethod === 'card' && (
        <div className="form-group">
          <label>Card Details</label>
          <div style={{ padding: '0.8rem', border: '1px solid #ddd', borderRadius: '4px' }}>
            <CardElement />
          </div>
        </div>
      )}

      {paymentMethod === 'upi' && (
        <div className="form-group">
          <label>UPI ID</label>
          <input
            type="text"
            placeholder="yourname@upi"
            value={upiId}
            onChange={(e) => setUpiId(e.target.value)}
            required
          />
          <div style={{ marginTop: '1rem', padding: '1rem', background: '#f8f9fa', borderRadius: '4px', textAlign: 'center' }}>
            <p style={{ margin: 0, color: '#7f8c8d' }}>Pay using any UPI app</p>
            <div style={{ display: 'flex', justifyContent: 'center', gap: '1rem', marginTop: '0.5rem' }}>
              <span>Google Pay</span>
              <span>ðŸ“± PhonePe</span>
              <span>ðŸ“± Paytm</span>
            </div>
          </div>
        </div>
      )}

      {paymentMethod === 'scanner' && (
        <div className="form-group">
          <label>Scan QR Code to Pay</label>
          <div style={{ 
            padding: '2rem', 
            background: '#f8f9fa', 
            borderRadius: '4px', 
            textAlign: 'center',
            border: '2px dashed #ddd'
          }}>
            {!showScanner ? (
              <>
                <div style={{ fontSize: '4rem', marginBottom: '1rem' }}>ðŸ“·</div>
                <button 
                  type="button"
                  onClick={() => setShowScanner(true)}
                  style={{ 
                    padding: '0.8rem 1.5rem', 
                    background: '#3498db', 
                    color: 'white', 
                    border: 'none', 
                    borderRadius: '4px',
                    cursor: 'pointer'
                  }}
                >
                  Open QR Scanner
                </button>
              </>
            ) : (
              <>
                <div style={{ 
                  width: '200px', 
                  height: '200px', 
                  margin: '0 auto', 
                  background: 'white', 
                  border: '2px solid #3498db',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontSize: '3rem'
                }}>
                  ðŸ“±
                </div>
                <p style={{ marginTop: '1rem', color: '#7f8c8d' }}>Scan QR code with your payment app</p>
                <p style={{ fontSize: '0.9rem', color: '#95a5a6' }}>Amount: ${project?.price}</p>
              </>
            )}
          </div>
        </div>
      )}

      <button type="submit" disabled={loading} className="btn-primary">
        {loading ? 'Processing...' : `Pay $${project?.price}`}
      </button>

      <div style={{ marginTop: '1rem', textAlign: 'center', fontSize: '0.9rem', color: '#7f8c8d' }}>
        ðŸ”’ Secure Payment â€¢ All transactions are encrypted
      </div>
    </form>
  );
};

const Payment = () => {
  const { projectId } = useParams();
  const [project, setProject] = useState(null);

  useEffect(() => {
    const fetchProject = async () => {
      try {
        const { data } = await api.get('/projects');
        const proj = data.find(p => p._id === projectId);
        setProject(proj);
      } catch (error) {
        console.error(error);
      }
    };
    fetchProject();
  }, [projectId]);

  return (
    <Elements stripe={stripePromise}>
      <CheckoutForm projectId={projectId} project={project} />
    </Elements>
  );
};

export default Payment;
