import React, { useState, useEffect } from 'react';
import api from '../utils/api';

const MyPurchases = () => {
  const [purchases, setPurchases] = useState([]);

  useEffect(() => {
    fetchPurchases();
  }, []);

  const fetchPurchases = async () => {
    try {
      const { data } = await api.get('/payments/user');
      setPurchases(data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleDownload = (projectId) => {
    window.location.href = `http://localhost:5000/api/projects/download/${projectId}`;
  };

  return (
    <div className="container">
      <h2>My Purchases</h2>
      <div className="projects-grid">
        {purchases.map(purchase => (
          <div key={purchase._id} className="project-card">
            <img 
              src={`http://localhost:5000/uploads/${purchase.projectId?.image}`} 
              alt={purchase.projectId?.title} 
            />
            <div className="project-card-content">
              <h3>{purchase.projectId?.title}</h3>
              <p>{purchase.projectId?.description}</p>
              <div className="price">${purchase.amount}</div>
              <p>Purchased: {new Date(purchase.createdAt).toLocaleDateString()}</p>
              <button onClick={() => handleDownload(purchase.projectId._id)}>
                Download Project
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MyPurchases;
