import React, { useState, useEffect } from 'react';
import api from '../utils/api';

const MyPurchases = () => {
  const [allPayments, setAllPayments] = useState([]);

  useEffect(() => {
    fetchPurchases();
  }, []);

  const fetchPurchases = async () => {
    try {
      const { data } = await api.get('/payments/user/all');
      console.log('Fetched payments:', data);
      setAllPayments(data);
    } catch (error) {
      console.error('Error fetching payments:', error);
      alert('Failed to load purchases');
    }
  };

  const handleDownload = (projectId) => {
    window.location.href = `http://localhost:5000/api/projects/download/${projectId}`;
  };

  const pending = allPayments.filter(p => p.status === 'pending');
  const rejected = allPayments.filter(p => p.status === 'rejected');
  const approved = allPayments.filter(p => p.status === 'completed');

  return (
    <div className="container">
      <h2>My Purchases</h2>

      {allPayments.length === 0 ? (
        <p style={{ textAlign: 'center', marginTop: '2rem', color: '#7f8c8d' }}>
          No purchases yet. Buy a project to see it here.
        </p>
      ) : (
        <>
          {pending.length > 0 && (
            <div style={{ marginBottom: '2rem' }}>
              <h3 style={{ color: '#f39c12' }}>‚è≥ Pending Verification</h3>
              <div className="projects-grid">
                {pending.map(payment => (
                  <div key={payment._id} className="project-card" style={{ opacity: 0.8 }}>
                    <img 
                      src={`http://localhost:5000/uploads/${payment.projectId?.image}`} 
                      alt={payment.projectId?.title} 
                    />
                    <div className="project-card-content">
                      <h3>{payment.projectId?.title}</h3>
                      <p>{payment.projectId?.description}</p>
                      <div className="price">‚Çπ{payment.amount}</div>
                      <p style={{ color: '#f39c12', fontWeight: 'bold' }}>‚è≥ Waiting for admin approval</p>
                      <p style={{ fontSize: '0.9rem', color: '#7f8c8d' }}>Submitted: {new Date(payment.createdAt).toLocaleDateString()}</p>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {rejected.length > 0 && (
            <div style={{ marginBottom: '2rem' }}>
              <h3 style={{ color: '#e74c3c' }}>‚ùå Rejected Payments</h3>
              <div className="projects-grid">
                {rejected.map(payment => (
                  <div key={payment._id} className="project-card" style={{ opacity: 0.7, border: '2px solid #e74c3c' }}>
                    <img 
                      src={`http://localhost:5000/uploads/${payment.projectId?.image}`} 
                      alt={payment.projectId?.title} 
                    />
                    <div className="project-card-content">
                      <h3>{payment.projectId?.title}</h3>
                      <p>{payment.projectId?.description}</p>
                      <div className="price">‚Çπ{payment.amount}</div>
                      <p style={{ color: '#e74c3c', fontWeight: 'bold' }}>‚ùå Payment Rejected</p>
                      <p style={{ fontSize: '0.9rem', color: '#7f8c8d' }}>Contact admin for details</p>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {approved.length > 0 && (
            <div>
              <h3 style={{ color: '#27ae60' }}>‚úÖ Approved & Ready to Download</h3>
              <div className="projects-grid">
                {approved.map(purchase => (
                  <div key={purchase._id} className="project-card">
                    <img 
                      src={`http://localhost:5000/uploads/${purchase.projectId?.image}`} 
                      alt={purchase.projectId?.title} 
                    />
                    <div className="project-card-content">
                      <h3>{purchase.projectId?.title}</h3>
                      <p>{purchase.projectId?.description}</p>
                      <div className="price">‚Çπ{purchase.amount}</div>
                      <p style={{ fontSize: '0.9rem', color: '#27ae60', fontWeight: 'bold' }}>‚úÖ Approved</p>
                      <p style={{ fontSize: '0.9rem', color: '#7f8c8d' }}>Purchased: {new Date(purchase.createdAt).toLocaleDateString()}</p>
                      <button onClick={() => handleDownload(purchase.projectId._id)}>
                        üì• Download Project
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default MyPurchases;
