import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../utils/api';

const Payment = () => {
  const { projectId } = useParams();
  const navigate = useNavigate();
  const [project, setProject] = useState(null);
  const [loading, setLoading] = useState(false);
  const [paymentMethod, setPaymentMethod] = useState('upi');
  const [transactionId, setTransactionId] = useState('');
  const [screenshot, setScreenshot] = useState(null);

  const PAYMENT_DETAILS = {
    upiId: '9123202975@axl',  
    phoneNumber: '9123202975',  
    qrCodeImage: '/pngtree.png'  
  };

  useEffect(() => {
    const fetchProject = async () => {
      try {
        const { data } = await api.get('/projects');
        const proj = data.find(p => p._id === projectId);
        setProject(proj);
      } catch (error) {
        console.error(error);
      }
    };
    fetchProject();
  }, [projectId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!transactionId) {
      alert('Please enter transaction ID');
      return;
    }

    setLoading(true);
    try {
      const formData = new FormData();
      formData.append('projectId', projectId);
      formData.append('transactionId', transactionId);
      formData.append('paymentMethod', paymentMethod);
      if (screenshot) {
        formData.append('screenshot', screenshot);
      }

      await api.post('/payments/manual-confirm', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });

      alert('Payment submitted! Admin will verify and approve.');
      navigate('/my-purchases');
    } catch (error) {
      alert('Payment submission failed');
    }
    setLoading(false);
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
    alert('Copied to clipboard!');
  };

  return (
    <div className="form-container" style={{ maxWidth: '600px' }}>
      <h2>Complete Payment</h2>
      {project && (
        <div style={{ marginBottom: '1.5rem', textAlign: 'center', padding: '1rem', background: '#f8f9fa', borderRadius: '8px' }}>
          <h3 style={{ margin: '0 0 0.5rem 0' }}>{project.title}</h3>
          <p className="price" style={{ margin: 0 }}>â‚¹{project.price}</p>
        </div>
      )}

      <div className="form-group">
        <label>Select Payment Method</label>
        <select 
          value={paymentMethod} 
          onChange={(e) => setPaymentMethod(e.target.value)}
          style={{ width: '100%', padding: '0.8rem', border: '1px solid #ddd', borderRadius: '4px', fontSize: '1rem' }}
        >
          <option value="upi">UPI (PhonePe/GPay/Paytm)</option>
          <option value="qr">QR Code Scanner</option>
        </select>
      </div>

      {paymentMethod === 'upi' && (
        <div style={{ marginBottom: '1.5rem' }}>
          <div style={{ padding: '1.5rem', background: '#e8f5e9', borderRadius: '8px', marginBottom: '1rem' }}>
            <h4 style={{ margin: '0 0 1rem 0', color: '#2c3e50' }}>Pay to this UPI ID:</h4>
            <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', marginBottom: '1rem' }}>
              <input 
                type="text" 
                value={PAYMENT_DETAILS.upiId} 
                readOnly 
                style={{ flex: 1, padding: '0.8rem', border: '2px solid #27ae60', borderRadius: '4px', fontSize: '1.1rem', fontWeight: 'bold' }}
              />
              <button 
                type="button"
                onClick={() => copyToClipboard(PAYMENT_DETAILS.upiId)}
                style={{ padding: '0.8rem 1rem', background: '#27ae60', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
              >
                Copy
              </button>
            </div>
            <p style={{ margin: 0, fontSize: '0.9rem', color: '#7f8c8d' }}>Or Phone: {PAYMENT_DETAILS.phoneNumber}</p>
          </div>
          <div style={{ padding: '1rem', background: '#fff3cd', borderRadius: '8px', marginBottom: '1rem' }}>
            <p style={{ margin: 0, fontSize: '0.9rem' }}>
              ðŸ“± <strong>Steps:</strong><br/>
              1. Open PhonePe/GPay/Paytm<br/>
              2. Send â‚¹{project?.price} to above UPI ID<br/>
              3. Copy Transaction ID and paste below<br/>
              4. Upload payment screenshot
            </p>
          </div>
        </div>
      )}

      {paymentMethod === 'qr' && (
        <div style={{ marginBottom: '1.5rem' }}>
          <div style={{ padding: '1.5rem', background: '#e3f2fd', borderRadius: '8px', textAlign: 'center' }}>
            <h4 style={{ margin: '0 0 1rem 0', color: '#2c3e50' }}>Scan QR Code to Pay</h4>
            <div style={{ 
              width: '250px', 
              height: '250px', 
              margin: '0 auto 1rem', 
              background: 'white', 
              border: '3px solid #3498db',
              borderRadius: '8px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              fontSize: '1rem',
              color: '#7f8c8d',
              padding: '1rem',
              boxSizing: 'border-box'
            }}>
              {/* Replace this with your actual QR code image */}
              <div>
                <div style={{ fontSize: '4rem', marginBottom: '0.5rem' }}>ðŸ“·</div>
                <p style={{ margin: 0 }}>Add your QR code image<br/>in public/payment-qr.png</p>
                {/* Uncomment below and add your QR image */}
                {/* <img src="/payment-qr.png" alt="Payment QR" style={{ width: '100%', height: '100%' }} /> */}
              </div>
            </div>
            <p style={{ margin: 0, fontSize: '1.2rem', fontWeight: 'bold', color: '#27ae60' }}>Amount: â‚¹{project?.price}</p>
          </div>
          <div style={{ padding: '1rem', background: '#fff3cd', borderRadius: '8px', marginBottom: '1rem' }}>
            <p style={{ margin: 0, fontSize: '0.9rem' }}>
              ðŸ“± <strong>Steps:</strong><br/>
              1. Open any UPI app (PhonePe/GPay/Paytm)<br/>
              2. Scan above QR code<br/>
              3. Pay â‚¹{project?.price}<br/>
              4. Enter Transaction ID below
            </p>
          </div>
        </div>
      )}

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Transaction ID / UTR Number *</label>
          <input
            type="text"
            placeholder="Enter transaction ID from payment app"
            value={transactionId}
            onChange={(e) => setTransactionId(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>Upload Payment Screenshot (Optional)</label>
          <input
            type="file"
            accept="image/*"
            onChange={(e) => setScreenshot(e.target.files[0])}
          />
          <p style={{ fontSize: '0.85rem', color: '#7f8c8d', margin: '0.5rem 0 0 0' }}>
            Upload screenshot for faster verification
          </p>
        </div>

        <button type="submit" disabled={loading} className="btn-primary">
          {loading ? 'Submitting...' : 'Submit Payment'}
        </button>
      </form>

      <div style={{ marginTop: '1.5rem', padding: '1rem', background: '#f8f9fa', borderRadius: '8px', fontSize: '0.9rem', color: '#7f8c8d', textAlign: 'center' }}>
        ðŸ”’ Your payment will be verified by admin within 24 hours
      </div>
    </div>
  );
};

export default Payment;
