import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import api from '../utils/api';

const stripePromise = loadStripe('your_stripe_publishable_key_here');

const CheckoutForm = ({ projectId, project }) => {
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!stripe || !elements) return;

    setLoading(true);
    try {
      const { data } = await api.post('/payments/create-payment-intent', { projectId });

      const result = await stripe.confirmCardPayment(data.clientSecret, {
        payment_method: { card: elements.getElement(CardElement) }
      });

      if (result.error) {
        alert(result.error.message);
      } else {
        await api.post('/payments/confirm', {
          projectId,
          paymentIntentId: result.paymentIntent.id
        });
        alert('Payment successful! Downloading...');
        window.location.href = `http://localhost:5000/api/projects/download/${projectId}`;
        navigate('/my-purchases');
      }
    } catch (error) {
      alert('Payment failed');
    }
    setLoading(false);
  };

  return (
    <form onSubmit={handleSubmit} className="form-container">
      <h2>Complete Payment</h2>
      {project && (
        <div style={{ marginBottom: '1.5rem' }}>
          <h3>{project.title}</h3>
          <p className="price">${project.price}</p>
        </div>
      )}
      <div className="form-group">
        <label>Card Details</label>
        <div style={{ padding: '0.8rem', border: '1px solid #ddd', borderRadius: '4px' }}>
          <CardElement />
        </div>
      </div>
      <button type="submit" disabled={!stripe || loading} className="btn-primary">
        {loading ? 'Processing...' : 'Pay Now'}
      </button>
    </form>
  );
};

const Payment = () => {
  const { projectId } = useParams();
  const [project, setProject] = useState(null);

  useEffect(() => {
    const fetchProject = async () => {
      try {
        const { data } = await api.get('/projects');
        const proj = data.find(p => p._id === projectId);
        setProject(proj);
      } catch (error) {
        console.error(error);
      }
    };
    fetchProject();
  }, [projectId]);

  return (
    <Elements stripe={stripePromise}>
      <CheckoutForm projectId={projectId} project={project} />
    </Elements>
  );
};

export default Payment;
