import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../utils/api';

const Home = ({ user }) => {
  const [projects, setProjects] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    try {
      const { data } = await api.get('/projects');
      setProjects(data);
    } catch (error) {
      console.error(error);
    }
  };

  const handlePayment = (projectId) => {
    if (!user) {
      alert('Please login first to purchase');
      navigate('/login');
    } else {
      navigate(`/payment/${projectId}`);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Delete this project?')) {
      try {
        await api.delete(`/projects/${id}`);
        fetchProjects();
      } catch (error) {
        alert('Error deleting project');
      }
    }
  };

  return (
    <>
      <div className="hero-section">
        <h1>üéØ Welcome to Project Store</h1>
        <p>Download Premium Quality Projects at Affordable Prices</p>
      </div>

      <div className="container">
        <div className="stats-section">
          <div className="stat-card">
            <h3>üöÄ {projects.length}+</h3>
            <p>Projects Available</p>
          </div>
          <div className="stat-card">
            <h3>‚ö° 100%</h3>
            <p>Secure Payment</p>
          </div>
          <div className="stat-card">
            <h3>üíØ 24/7</h3>
            <p>Support Available</p>
          </div>
          <div className="stat-card">
            <h3>üì¶ Instant</h3>
            <p>Download Access</p>
          </div>
        </div>

        <h2 style={{ marginBottom: '1rem', color: '#2c3e50' }}>üî• Featured Projects</h2>
        {projects.length === 0 ? (
          <div style={{ 
            textAlign: 'center', 
            padding: '4rem 2rem',
            background: 'white',
            borderRadius: '12px',
            boxShadow: '0 4px 12px rgba(0,0,0,0.1)'
          }}>
            <div style={{ fontSize: '4rem', marginBottom: '1rem' }}>üìÇ</div>
            <p style={{ color: '#7f8c8d', fontSize: '1.2rem' }}>
              No projects available yet.
            </p>
            {user?.role === 'admin' && (
              <button 
                onClick={() => navigate('/admin/add-project')}
                style={{
                  marginTop: '1rem',
                  padding: '1rem 2rem',
                  background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                  color: 'white',
                  border: 'none',
                  borderRadius: '8px',
                  cursor: 'pointer',
                  fontSize: '1rem',
                  fontWeight: '600'
                }}
              >
                ‚ûï Add Your First Project
              </button>
            )}
          </div>
        ) : (
          <div className="projects-grid">
            {projects.map(project => (
              <div key={project._id} className="project-card">
                <img src={`http://localhost:5000/uploads/${project.image}`} alt={project.title} />
                <div className="project-card-content">
                  <h3>{project.title}</h3>
                  <p>{project.description}</p>
                  <div className="price">‚Çπ{project.price}</div>
                  <a href={project.liveLink} target="_blank" rel="noopener noreferrer">
                    üîó View Live Demo ‚Üí
                  </a>
                  {user?.role !== 'admin' && (
                    <button onClick={() => handlePayment(project._id)}>
                      üõí Purchase & Download
                    </button>
                  )}
                  {user?.role === 'admin' && (
                    <button className="btn-danger" onClick={() => handleDelete(project._id)}>
                      üóëÔ∏è Delete Project
                    </button>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}

        <div style={{
          marginTop: '4rem',
          padding: '3rem 2rem',
          background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
          borderRadius: '12px',
          color: 'white',
          textAlign: 'center'
        }}>
          <h2 style={{ marginBottom: '1rem' }}>üí° Why Choose Us?</h2>
          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
            gap: '2rem',
            marginTop: '2rem'
          }}>
            <div>
              <div style={{ fontSize: '3rem', marginBottom: '0.5rem' }}>‚úÖ</div>
              <h4>Quality Code</h4>
              <p style={{ opacity: 0.9 }}>Clean & documented</p>
            </div>
            <div>
              <div style={{ fontSize: '3rem', marginBottom: '0.5rem' }}>üîí</div>
              <h4>Secure Payment</h4>
              <p style={{ opacity: 0.9 }}>UPI & QR supported</p>
            </div>
            <div>
              <div style={{ fontSize: '3rem', marginBottom: '0.5rem' }}>‚ö°</div>
              <h4>Instant Access</h4>
              <p style={{ opacity: 0.9 }}>Download immediately</p>
            </div>
            <div>
              <div style={{ fontSize: '3rem', marginBottom: '0.5rem' }}>üí¨</div>
              <h4>24/7 Support</h4>
              <p style={{ opacity: 0.9 }}>Always here to help</p>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Home;
