import React, { useState, useEffect } from 'react';
import api from '../utils/api';

const AdminPayments = () => {
  const [payments, setPayments] = useState([]);

  useEffect(() => {
    fetchPayments();
  }, []);

  const fetchPayments = async () => {
    try {
      const { data } = await api.get('/payments/admin/all');
      setPayments(data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleApprove = async (id) => {
    try {
      await api.put(`/payments/approve/${id}`);
      alert('Payment approved!');
      fetchPayments();
    } catch (error) {
      alert('Error approving payment');
    }
  };

  const handleReject = async (id) => {
    if (window.confirm('Reject this payment?')) {
      try {
        await api.put(`/payments/reject/${id}`);
        alert('Payment rejected!');
        fetchPayments();
      } catch (error) {
        alert('Error rejecting payment');
      }
    }
  };

  return (
    <div className="container">
      <h2>All Payment Details</h2>
      <div className="table-container">
        <table>
          <thead>
            <tr>
              <th>User</th>
              <th>Phone</th>
              <th>Project</th>
              <th>Amount</th>
              <th>Method</th>
              <th>Transaction ID</th>
              <th>Status</th>
              <th>Screenshot</th>
              <th>Date</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {payments.map(payment => (
              <tr key={payment._id}>
                <td>{payment.userId?.name}<br/><small>{payment.userId?.email}</small></td>
                <td>{payment.userId?.phone}</td>
                <td>{payment.projectId?.title}</td>
                <td>â‚¹{payment.amount}</td>
                <td>{payment.paymentMethod}</td>
                <td>{payment.stripePaymentId}</td>
                <td>
                  <span style={{ 
                    padding: '0.3rem 0.6rem', 
                    borderRadius: '4px', 
                    fontSize: '0.85rem',
                    background: payment.status === 'completed' ? '#d4edda' : payment.status === 'pending' ? '#fff3cd' : '#f8d7da',
                    color: payment.status === 'completed' ? '#155724' : payment.status === 'pending' ? '#856404' : '#721c24'
                  }}>
                    {payment.status}
                  </span>
                </td>
                <td>
                  {payment.screenshot && (
                    <a href={`http://localhost:5000/uploads/${payment.screenshot}`} target="_blank" rel="noopener noreferrer">
                      View
                    </a>
                  )}
                </td>
                <td>{new Date(payment.createdAt).toLocaleDateString()}</td>
                <td>
                  {payment.status === 'pending' && (
                    <>
                      <button 
                        onClick={() => handleApprove(payment._id)}
                        style={{ padding: '0.4rem 0.8rem', background: '#27ae60', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer', marginRight: '0.5rem' }}
                      >
                        Approve
                      </button>
                      <button 
                        onClick={() => handleReject(payment._id)}
                        style={{ padding: '0.4rem 0.8rem', background: '#e74c3c', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
                      >
                        Reject
                      </button>
                    </>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminPayments;
