import React from 'react';
import { useNavigate } from 'react-router-dom';

const Navbar = ({ user, setUser }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    navigate('/');
  };

  return (
    <nav className="navbar">
      <h1 onClick={() => navigate('/')}>üöÄ Project Store</h1>
      <div className="navbar-buttons">
        {user ? (
          <div className="navbar-user">
            <span>
              üë§ {user.name} ({user.role})
            </span>
            {user.role === 'admin' && (
              <>
                <button onClick={() => navigate('/admin/add-project')}>‚ûï Add Project</button>
                <button onClick={() => navigate('/admin/payments')}>üí≥ Payments</button>
              </>
            )}
            {user.role === 'user' && (
              <button onClick={() => navigate('/my-purchases')}>üì¶ My Purchases</button>
            )}
            <button onClick={handleLogout}>üö™ Logout</button>
          </div>
        ) : (
          <>
            <button onClick={() => navigate('/admin/login')}>üîê Admin</button>
            <button onClick={() => navigate('/login')}>üë§ Login</button>
            <button onClick={() => navigate('/register')}>üìù Register</button>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
