import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const Navbar = ({ user, setUser }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  useEffect(() => {
    setIsMenuOpen(false);
  }, [location]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    navigate('/');
    setIsMenuOpen(false);
  };

  const handleNavigation = (path) => {
    navigate(path);
    setIsMenuOpen(false);
  };

  return (
    <nav className="navbar">
      <h1 onClick={() => handleNavigation('/')}>üöÄ Project Store</h1>

      <button className="menu-toggle" onClick={() => setIsMenuOpen(!isMenuOpen)}>
        {isMenuOpen ? '‚úï' : '‚ò∞'}
      </button>

      <div className={`navbar-buttons ${isMenuOpen ? 'active' : ''}`}>
        {user ? (
          <div className="navbar-user">
            <span>
              üë§ {user.name} ({user.role})
            </span>
            {user.role === 'admin' && (
              <>
                <button onClick={() => handleNavigation('/admin/add-project')}>‚ûï Add Project</button>
                <button onClick={() => handleNavigation('/admin/payments')}>üí≥ Payments</button>
              </>
            )}
            {user.role === 'user' && (
              <button onClick={() => handleNavigation('/my-purchases')}>üì¶ My Purchases</button>
            )}
            <button onClick={handleLogout}>üö™ Logout</button>
          </div>
        ) : (
          <>
            <button onClick={() => handleNavigation('/admin/login')}>üîê Admin</button>
            <button onClick={() => handleNavigation('/login')}>üë§ Login</button>
            <button onClick={() => handleNavigation('/register')}>üìù Register</button>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
