import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Navbar = ({ user, setUser }) => {
  const navigate = useNavigate();
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    navigate('/');
    setIsMenuOpen(false);
  };

  return (
    <nav className="navbar">
      <h1 onClick={() => navigate('/')}>üöÄ Project Store</h1>

      <button className="menu-toggle" onClick={() => setIsMenuOpen(!isMenuOpen)}>
        {isMenuOpen ? '‚úï' : '‚ò∞'}
      </button>

      <div className={`navbar-buttons ${isMenuOpen ? 'active' : ''}`}>
        {user ? (
          <div className="navbar-user">
            <span>
              üë§ {user.name} ({user.role})
            </span>
            {user.role === 'admin' && (
              <>
                <button onClick={() => { navigate('/admin/add-project'); setIsMenuOpen(false); }}>‚ûï Add Project</button>
                <button onClick={() => { navigate('/admin/payments'); setIsMenuOpen(false); }}>üí≥ Payments</button>
              </>
            )}
            {user.role === 'user' && (
              <button onClick={() => { navigate('/my-purchases'); setIsMenuOpen(false); }}>üì¶ My Purchases</button>
            )}
            <button onClick={handleLogout}>üö™ Logout</button>
          </div>
        ) : (
          <>
            <button onClick={() => { navigate('/admin/login'); setIsMenuOpen(false); }}>üîê Admin</button>
            <button onClick={() => { navigate('/login'); setIsMenuOpen(false); }}>üë§ Login</button>
            <button onClick={() => { navigate('/register'); setIsMenuOpen(false); }}>üìù Register</button>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
